/**********************************************
 ** MOBILE NAVIGATION
 * *******************************************/
document.addEventListener("DOMContentLoaded", function () {
  var toggle = document.querySelector('.header-mobile--toggle-box'); // Mobile toggle button

  var mobileNavigation = document.querySelector(".header-mobile-top"); // mobile navgation wrapp

  toggle && mobileToggle(toggle);
  mobileNavigation && stickyMobileNavigation(mobileNavigation);
});
/** Show-hide mobile navigation */

function mobileToggle(toggle) {
  toggle.addEventListener('click', function () {
    toggle.classList.toggle('header-mobile--toggle-box--active');
    document.querySelector('.mobile-nav').classList.toggle("show");
  });
}
/** Set sticky class on scroll */


function stickyMobileNavigation(mobileNavigation) {
  var scrollpos;
  var mobileHeaderHeight = mobileNavigation.offsetHeight;

  var add_sticky_class = function add_sticky_class() {
    return mobileNavigation.classList.add("header-mobile-top--sticky");
  };

  var remove_sticky_class = function remove_sticky_class() {
    return mobileNavigation.classList.remove("header-mobile-top--sticky");
  };

  window.addEventListener('scroll', function () {
    scrollpos = window.scrollY;
    scrollpos >= mobileHeaderHeight ? add_sticky_class() : remove_sticky_class();
  });
}